import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [tools, setTools] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTools();
  }, []);

  const fetchTools = () => {
    axios
      .get("http://localhost:8080/tools")
      .then((response) => {
        // Convert object response into an array of objects
        const toolsArray = Object.entries(response.data).map(([name, votes]) => ({ name, votes }));
        setTools(toolsArray);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError("Failed to fetch tools. Please check the backend.");
      });
  };

  const handleVote = (toolName) => {
    axios
      .post(`http://localhost:8080/vote/${toolName}`)
      .then(() => {
        // Update the vote count directly in state
        setTools((prevTools) =>
          prevTools.map((tool) =>
            tool.name === toolName ? { ...tool, votes: tool.votes + 1 } : tool
          )
        );
      })
      .catch((error) => {
        console.error("Error voting:", error);
        setError("Failed to vote. Please try again.");
      });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Vote for Your Favourite DevOps Tools</h1>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {tools.length > 0 ? (
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {tools.map((tool) => (
            <li key={tool.name} style={{ margin: "10px 0" }}>
              {tool.name} - Votes: {tool.votes}
              <button style={{ marginLeft: "10px" }} onClick={() => handleVote(tool.name)}>
                Vote
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tools found. Please check the backend.</p>
      )}
    </div>
  );
}

export default App;
