import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [tools, setTools] = useState([]);

  useEffect(() => {
    // Fetch data from the backend
    axios.get("http://localhost:8080/tools")
      .then(response => {
        setTools(response.data);
      })
      .catch(error => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Vote for Your Favourite DevOps Tools</h1>
      {tools.length > 0 ? (
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {tools.map(tool => (
            <li key={tool.name} style={{ margin: "10px 0" }}>
              {tool.name} - Votes: {tool.votes}
              <button
                style={{ marginLeft: "10px" }}
                onClick={() => {
                  axios.post(`http://localhost:8080/vote/${tool.name}`)
                    .then(() => {
                      // Refetch data after voting
                      axios.get("http://localhost:8080/tools")
                        .then(response => {
                          setTools(response.data);
                        })
                        .catch(error => {
                          console.error("Error refetching data:", error);
                        });
                    })
                    .catch(error => {
                      console.error("Error voting:", error);
                      alert("Failed to vote. Please try again.");
                    });
                }}
              >
                Vote
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tools found. Please check the backend.</p>
      )}
    </div>
  );
}

export default App;
